import javax.swing.JOptionPane;public class HelloWorldImagem{	public static void main(String args[]) throws Exception {                                String escolha = null;                escolha = JOptionPane.showInputDialog("Digite cinza para imagens tons de cinza \n ou colorida para imagens colorida",escolha);                                        escolha = escolha.toUpperCase();                                switch(escolha){                    case "COLORIDA":                            String caminho = ImagemUtil.selecionarCaminhoDaImagem();                            int img[][][] = ImagemUtil.abrirImagemColorida(caminho);                            ImagemUtil.visualizarImagem(img, "Teste..");                    break;                    case "CINZA":                            String caminho2 = ImagemUtil.selecionarCaminhoDaImagem();                            int img2[][] = ImagemUtil.abrirImagemTonsDeCinza(caminho2);                            ImagemUtil.visualizarImagem(img2, "Teste..");                    break;                    default:                      JOptionPane.showMessageDialog(null, "Tente novamente e digite uma opção valida");                     break;                }           	}}